using TechStore.Models;
using System.Security.Cryptography;
using System.Text;
using Microsoft.EntityFrameworkCore;

namespace TechStore.Data;

public class UsuarioService
{
    private readonly TechStoreContext _dbContext;
    public Usuario? UsuarioActual { get; set; }

    public UsuarioService(TechStoreContext dbContext)
    {
        _dbContext = dbContext;
    }

    public async Task<(bool √©xito, string mensaje)> RegistrarAsync(string email, string nombre, string apellido, string contrase√±a, string contrase√±aConfirm)
    {
        if (contrase√±a != contrase√±aConfirm)
            return (false, "Las contrase√±as no coinciden");

        if (contrase√±a.Length < 6)
            return (false, "La contrase√±a debe tener al menos 6 caracteres");

        var usuarioExistente = await _dbContext.Usuarios.FirstOrDefaultAsync(u => u.Email == email);
        if (usuarioExistente != null)
            return (false, "El email ya est√° registrado");

        var usuario = new Usuario
        {
            Email = email,
            Nombre = nombre,
            Apellido = apellido,
            Contrase√±a = HashContrase√±a(contrase√±a),
            FechaRegistro = DateTime.Now
        };

        _dbContext.Usuarios.Add(usuario);
        await _dbContext.SaveChangesAsync();

        UsuarioActual = usuario;
        return (true, "Registro exitoso");
    }

    public async Task<(bool √©xito, string mensaje)> LoginAsync(string email, string contrase√±a)
    {
        var usuario = await _dbContext.Usuarios
            .FirstOrDefaultAsync(u => u.Email == email && u.EstaActivo);

        if (usuario == null)
            return (false, "Email o contrase√±a incorrectos");

        if (!VerificaContrase√±a(contrase√±a, usuario.Contrase√±a))
            return (false, "Email o contrase√±a incorrectos");

        usuario.UltimoLogin = DateTime.Now;
        await _dbContext.SaveChangesAsync();

        UsuarioActual = usuario;
        return (true, "Login exitoso");
    }

    public async Task LogoutAsync()
    {
        UsuarioActual = null;
        await Task.CompletedTask;
    }

    public async Task<Usuario?> ObtenerUsuarioPorIdAsync(int id)
    {
        return await _dbContext.Usuarios
            .Include(u => u.Ordenes)
            .FirstOrDefaultAsync(u => u.Id == id);
    }

    public async Task<bool> ActualizarPerfilAsync(int id, string nombre, string apellido, string telefono, string direcci√≥n)
    {
        var usuario = await _dbContext.Usuarios.FindAsync(id);
        if (usuario == null)
            return false;

        usuario.Nombre = nombre;
        usuario.Apellido = apellido;
        usuario.Telefono = telefono;
        usuario.Direcci√≥n = direcci√≥n;

        await _dbContext.SaveChangesAsync();
        UsuarioActual = usuario;
        return true;
    }

    public async Task<bool> CambiarContrase√±aAsync(int id, string contrase√±aActual, string contrase√±aNueva, string contrase√±aConfirm)
    {
        var usuario = await _dbContext.Usuarios.FindAsync(id);
        if (usuario == null)
            return false;

        if (!VerificaContrase√±a(contrase√±aActual, usuario.Contrase√±a))
            return false;

        if (contrase√±aNueva != contrase√±aConfirm)
            return false;

        if (contrase√±aNueva.Length < 6)
            return false;

        usuario.Contrase√±a = HashContrase√±a(contrase√±aNueva);
        await _dbContext.SaveChangesAsync();
        return true;
    }

    private string HashContrase√±a(string contrase√±a)
    {
        using (var sha256 = SHA256.Create())
        {
            var hashedBytes = sha256.ComputeHash(Encoding.UTF8.GetBytes(contrase√±a));
            return Convert.ToBase64String(hashedBytes);
        }
    }

    private bool VerificaContrase√±a(string contrase√±a, string hash)
    {
        var hashDelInput = HashContrase√±a(contrase√±a);
        return hashDelInput == hash;
    }
}

