namespace TechStore.Models;

public class ItemOrden
{
    public int Id { get; set; }
    public int ProductoTecnologicoId { get; set; }
    public ProductoTecnologico? ProductoTecnologico { get; set; }
    public int Cantidad { get; set; }
    public decimal PrecioBaseProducto { get; set; }
    public List<EspecificacionOrden> EspecificacionesSeleccionadas { get; set; } = new();

    public decimal PrecioTotal =>
        PrecioBaseProducto + EspecificacionesSeleccionadas.Sum(e => e.PrecioEspecificacion);
}

Components/Layout/MainLayout.razor.css.content:
.page {
    min-height: 100vh;
    display: flex;
    flex-direction: column;
}

.top-bar {
    background: linear-gradient(135deg, #0f172a 0%, #1e293b 100%);
    padding: 1rem 0;
    box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.3);
    position: sticky;
    top: 0;
    z-index: 100;
}

.top-bar .container {
    display: flex;
    align-items: center;
    justify-content: space-between;
}

.logo {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    text-decoration: none;
    color: white;
    font-weight: 700;
    font-size: 1.5rem;
    transition: transform 0.2s;
}

.logo:hover {
    transform: scale(1.05);
}

.logo-icon {
    font-size: 2rem;
}

.nav-menu {
    display: flex;
    gap: 2rem;
}

.nav-link {
    color: rgba(255, 255, 255, 0.8);
    text-decoration: none;
    font-weight: 500;
    padding: 0.5rem 1rem;
    border-radius: 0.5rem;
    transition: all 0.2s;
}

.nav-link:hover {
    color: white;
    background: rgba(255, 255, 255, 0.1);
}

.nav-link.active {
    color: #06b6d4;
    background: rgba(6, 182, 212, 0.1);
}

.cart-summary {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    background: rgba(255, 255, 255, 0.1);
    padding: 0.75rem 1.5rem;
    border-radius: 2rem;
    cursor: pointer;
    transition: all 0.2s;
}

.cart-summary:hover {
    background: rgba(255, 255, 255, 0.15);
    transform: translateY(-2px);
}

.cart-icon {
    font-size: 1.5rem;
}

.cart-count {
    background: #06b6d4;
    color: white;
    padding: 0.25rem 0.75rem;
    border-radius: 1rem;
    font-weight: 600;
    font-size: 0.875rem;
}

.cart-total {
    color: white;
    font-weight: 600;
    font-size: 1.125rem;
}

.main-content {
    flex: 1;
    padding: 2rem 0;
}

.footer {
    background: #0f172a;
    color: rgba(255, 255, 255, 0.6);
    padding: 2rem 0;
    text-align: center;
    margin-top: auto;
}
